! ( function ( c ) {
	c( '.tc-file-input-select' ).click( function ( e ) {
		e.preventDefault();
		var i = wp
			.media( { title: tcStrings.upload_message, multiple: ! 1 } )
			.open()
			.on( 'select', function ( e ) {
				var t = i.state().get( 'selection' ).first().toJSON().url;
				c( '.tc-file-input' ).val( t );
			} );
	} ),
		c( '.tc-file-input-reset' ).click( function ( e ) {
			e.preventDefault(), c( '.tc-file-input' ).attr( 'value', '' );
		} ),
		c( '#twitterCardType' )
			.on( 'change', function () {
					'player' === this.value
						? c( '*[class^="cardPlayer"]' ).show()
						: c( '*[class^="cardPlayer"]' ).hide();
			} )
			.change();
} )( jQuery );
